/**
 * Utility API for service checking etc.
*/

import WebServiceProxy from "./../Framework/WebServiceProxy";


export class utilSync extends WebServiceProxy {

    /**
     * Ping the service to confirm service active.
     *
     * @return string
     */
    public ping():string{
        return this.callMethod("ping", "GET", {  },null,"string");
    }


}


export class utilASync extends WebServiceProxy {

    /**
     * Ping the service to confirm service active.
     *
     * @return string
     */
    public ping():Promise<string>{
        return this.callMethod("ping", "GET", {  },null,"string");
    }


}




