/**
 * Account summary.  Used for listing accounts in both Admin and for a user.
*/

import SerialisableObject from "../../../Framework/SerialisableObject";

export default class AccountSummary extends SerialisableObject {

    /**
     * The account name - optional
     *
     * @var string 
     */
    private _name?:string;

    /**
     * Auto increment id.  Strategically breaking naming convention to
     * enforce security based upon account id.
     *
     * @var number 
     */
    private _accountId?:number;

    /**
     *
     * @var number 
     */
    private _parentAccountId?:number;

    /**
     * Status of the account in question.
     *
     * @var string 
     */
    private _status?:string;



    /**
     * Constructor
     *
     */
    constructor(){

        super();

        
    }

    /**
     * Get the name
     *
     * @return string
     */
    public get name():string | undefined{

        
        return this._name;
    }


    /**
     * Get the accountId
     *
     * @return number
     */
    public get accountId():number | undefined{

        return Number(this._accountId);
        
    }


    /**
     * Get the parentAccountId
     *
     * @return number
     */
    public get parentAccountId():number | undefined{

        return Number(this._parentAccountId);
        
    }


    /**
     * Get the status
     *
     * @return string
     */
    public get status():string | undefined{

        
        return this._status;
    }



}
