/**
 * Create descriptor, used for creating sites from e.g. API etc.
*/

import SerialisableObject from "../../../Framework/SerialisableObject";

export default class SiteCreateDescriptor extends SerialisableObject {

    /**
     * The title for this site create descriptor.
     *
     * @var string 
     */
    private _title?:string;

    /**
     * The siteKey for this site create descriptor.
     *
     * @var string 
     */
    private _siteKey?:string;


    /**
     * Constructor
     *
     * @param title
     * @param siteKey
     */
    constructor(title?:string, siteKey?:string){

        super();

        this._title = title;
        this._siteKey = siteKey;
        
    }

    /**
     * Get the title
     *
     * @return string
     */
    public get title():string | undefined{

        
        return this._title;
    }


    /**
     * Set the title
     *
     * @param string title
     * @return SiteCreateDescriptor
     */
    public set Title(title:string | undefined){
        this._title = title;
    }

    /**
     * Get the siteKey
     *
     * @return string
     */
    public get siteKey():string | undefined{

        
        return this._siteKey;
    }


    /**
     * Set the siteKey
     *
     * @param string siteKey
     * @return SiteCreateDescriptor
     */
    public set SiteKey(siteKey:string | undefined){
        this._siteKey = siteKey;
    }


}
