import {APIProviderASync, APIProviderSync} from "../../src/APIProvider";
import AccountSummary from "../../src/Kinicart/Objects/Account/AccountSummary";

describe('Tests for the account service', function () {

    let provider = new APIProviderSync(<string>process.env.SITE_ATOMIC_ENDPOINT, "TESTAPIKEY", "TESTAPISECRET");

    it('Can get my account summary details', function () {

        let accountSummary = provider.account().getAccountSummary();

        expect(accountSummary instanceof AccountSummary).toBeTruthy();
        expect(accountSummary.name).toEqual("Sam Davis Design");
        expect(accountSummary.accountId).toEqual(1);

    });


});
